mapper.update:
UPDATE CAREER_INFO SET
COMPANY_NAME = #{companyName}
, OCCUPATION = #{occupation}
, ASSIGNMENT_TASK = #{assignmentTask}
, EX_HIREDATE = TO_DATE(#{exHiredate,mode=IN,javaType=String,jdbcType=DATE},'YYYY/MM/DD')
, EX_RETIREMENT_DATE = TO_DATE(#{exRetirementDate,mode=IN,javaType=String,jdbcType=DATE},'YYYY/MM/DD')
WHERE EMP_CODE = #{empCode} 
and CAREER_CODE = #{careerCode}

mapper.delete:
DELETE FROM CAREER_INFO 
WHERE EMP_CODE = #{empCode} 
AND CAREER_CODE = #{careerCode}
