mapper.update:
UPDATE EDUCATION_INFO SET
SCHOOL_NAME= #{schoolName} 
, MAJOR = #{major}
, ENTRANCE_DATE = TO_DATE(#{entranceDate,mode=IN,javaType=String,jdbcType=DATE},'YYYY/MM/DD')
, GRADUATE_DATE = TO_DATE(#{graduateDate,mode=IN,javaType=String,jdbcType=DATE},'YYYY/MM/DD')
, GRADE = #{grade} 
WHERE EMP_CODE = #{empCode} 
and EDUCATION_CODE = #{educationCode}

mapper.delete:
DELETE FROM EDUCATION_INFO 
WHERE EMP_CODE = #{empCode} 
AND EDUCATION_CODE = #{educationCode}
